import axios from 'axios';

function App() {
  const [data,setData] = ({});
  const [location,setLocation] = ("");

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=aea2287ca7b92727a4b5453fbc8b955d`;

  const searchLocatition = (event) => {
    if(event.key ===Enter) {
      axios.get(url).then((response) => {
        setData(response.data)

      })
    }
    }



    return(
    <div className="app">
      <div className="search">
        <input
        value={location}
        >

        </input>
      </div>

      <div className="container">
        <div className="top">
          <div className="location">
            <p>Dallas</p>
          </div>
          <div className="temp">
            <h1>61°F</h1>
          </div>
          <div className="description">
          <p>Cloudy</p>
          </div>
        </div>
        <div className="bottom">
          <div className="feels">
            <p className="bold">61°F</p>
            <p>Feels Like</p>
          </div>
          <div className="humidty">
            <p className="bold">20%°F</p>
            <p>Humidity</p>
          </div>
          <div className="wind">
            <p className="bold">12 MPH</p>
            <p>Wind Speed</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
